class Solution {
public:
    int maxOperations(string s) {
        int n=s.size(),ans=0,cnt=0;
        int i=0;
        bool flag=false;
        while(i<n){
            if(s[i]=='1'){
                cnt++;
                flag=false;
                i++;
            }
            while(s[i]=='0'){
                i++;
                flag=true;
            }
            if(flag)ans+=cnt;
        }
        return ans;
    }
};
