class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans = 0, cnt = (s[0] == '1');
        for(int i = 1;i < n;i++){
            if(s[i] == '0'){
                if(s[i-1] != '0') ans += cnt;
            }
            else{
                cnt++;
            }
        }
        return ans;
    }
};
