class Solution {
    public int maxOperations(String s) {
        int ans =0 ;
        int n = s.length();
        int c=0;
        int l=0;
        for(int i=n-1;i>=0;i--){
            if(s.charAt(i) == '0' && l ==0){
                c++;
                l = 1;
            } 
            if(s.charAt(i) == '1') {
                ans += c;
                l=0;
            }
        }
        return ans;
    }
}
