class Solution:
    def maxOperations(self, s: str) -> int:
        # not on last index
        # curr = 1, next = 0
        n = len(s)
        count = 0
        ans = 0
        i = 0
        while i < n:
            if s[i] == '1':
                count += 1
            else: 
                while i+1 < n and s[i+1] == '0':
                    i += 1
                ans += count
            i += 1
        return ans
