#include <string>
#include <vector>
using namespace std;

class Solution {
public:
    int maxOperations(string str) {
        int result = 0;
        int count = 0;
        bool foundOne = false;
        vector<int> bitList;

        int index = 0;
        while (index < str.length()) {
            char ch = str[index];
            if (ch == '1') {
                bitList.push_back(1);
                foundOne = true;
            } else if (ch == '0' && foundOne) {
                bitList.push_back(0);
                foundOne = false;
            }
            index++;
        }

        index = 0;
        while (index < bitList.size()) {
            int bitValue = bitList[index];
            if (bitValue == 1) {
                count += 1;
            } else {
                result += count;
            }
            index++;
        }

        return result;
    }
};
