class Solution {
public:
    int maxOperations(string s) {
        int gap = 0;
        int cnt = 0;
        int n = s.size();
        char prev = s[n-1];
        int oc = 0;
        for(int i = n-2; i>=0; i--){
            if(prev == '0' && s[i] == '1'){
                oc = 1;
                gap++;
            }
            else if(prev == '1' && s[i] == '1'){
                oc++;
            }
            else if(prev == '0' && s[i] == '0'){
                continue;
            }
            else if(prev == '1' && s[i] == '0'){
                // cout << oc << " " << gap << endl;
                cnt += (oc*gap);
                oc = 0;
                // gap++;
            }
            prev = s[i];
        }
        // cout << oc << " " << gap;
        if(s[0] == '1') cnt += oc*gap;
        return cnt;
    }
};
