class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int cnt = 0;
        int i = 0;
        char[] ch = s.toCharArray();
        while (i < s.length()) {
            if (ch[i] == '0') {
                i++;
            } else {
                break;
            }
        }
        while (i < s.length()) {
            while (i < s.length()) {
                if (ch[i] == '1') {
                    i++;
                    cnt++;
                } else {
                    break;
                }
            }
            if (i == s.length()) {
                return ans;
            }
            ans += cnt;
            while (i < s.length()) {
                if (ch[i] == '0') {
                    i++;
                } else {
                    break;
                }
            }
        }
        return ans;
    }
}
