import java.util.ArrayList;
import java.util.List;

class Solution {


    public static int kush(String s){

        List<Integer> listee = new ArrayList<>();
        int manes = 0;
        boolean kushgsra = false;
        int bdsjsdj = 0;
        int taker = 2;

        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '1') {
                listee.add(1);
                taker++;
                kushgsra = true;
            }
            if (s.charAt(i) == '0' && kushgsra) {
                listee.add(0);
                taker--;
                kushgsra = false;
            }
        }

        for (int myval : listee) {
            if (myval == 1)   bdsjsdj++;
            else manes += bdsjsdj;  
        }
        taker = 0;

        return manes - taker;

    }
    public int maxOperations(String s) {
       int result =  kush(s);
       return result;

    }
}
