class Solution {
public:
    int maxOperations(string s) {
        string modify = "";
        int n = s.size();
        int i = 0;
        while(i<n){
            char cur = s[i];
            if(cur  == '0'){
                modify += s[i];
                while(i<n && s[i] == cur){
                    i++;
                }
            }else{
                modify += s[i];
                i++;
            }
        }
        int ans = 0;
        int ones = 0;
        s = modify;
        n = modify.size();
        for(int i=0;i<n;i++){
            if(s[i] == '0'){
                ans += ones;
            }else{
                ones++;
            }
        }
        return ans;
    }
};
