class Solution {
    public int maxOperations(String s) {
        int cur = 0, ct = 1, total = 0;

        while (cur < s.length() - 1) {
            if (s.charAt(cur) == '0') {
                cur++;
            } else {
                if (s.charAt(cur + 1) == '1') {
                    ct++;
                    cur++;
                } else {
                    cur++;
                    while (cur < s.length() && s.charAt(cur) != '1') {
                        cur++;
                    }
                    total += ct;
                    ct++;
                }
            }
        }

        return total;
    }
}
