class Solution {
public:
    int maxOperations(string s) {
        int cnt=0;
        int ans=0;
        if(s[0]=='1'){
            cnt++;
        }
        int n=s.size();
        for(int i=1;i<n;i++){
            if(s[i]=='0' && s[i-1]!='0'){
                ans+=cnt;
            }
            if(s[i]=='1'){
                cnt++;
            }
        }
        return ans;
    }
};
