class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();

        int ans = 0;
        int cnt = 0;
        char prev = '9';

        for(int i=n-1; i>=0; i--){
            if(s[i]=='0' and prev != '0') cnt++;
            if(s[i]=='1') ans += cnt;
            prev = s[i];
        }

        return ans;
    }
};
