class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int ones = 0;
        boolean flag = false;
        for(char c: s.toCharArray()){
            if(c=='1'){
                ones++;
                flag = true;
            }else if(c=='0' && flag){
                ans+=ones;
                flag = false;
            }
        }
        return ans;
    }
}
