class Solution {
    public int maxOperations(String s) {

        boolean f = false;
        int ans = 0;
        int cur = 0;

        int index = 0;

        while (index < s.length()) {

            if (s.charAt(index) == '1') {
                cur++;
                f = false;
            }

            if (s.charAt(index) == '0') {
                if (!f) {
                    ans += cur;
                }

                f = true;
                // cur = ans;
            }
            index++;
        }
        return ans;
    }
}
