import java.util.ArrayList;

class Solution {
    public int maxOperations(String s) {
        int n=s.length();
        char[] charArray = s.toCharArray();
        ArrayList<int[]> list=new ArrayList<>();
        for(int i=0;i<n; ){
            while(i<n && charArray[i]=='0') i++;
            if(i>=n)break;
            int j=i;
            while(j<n&&charArray[j]=='1'){
                ++j;
            }
            list.add(new int[]{i,j-1});
            i=j+1;
        }

        int cnt=0;
        int pre=0;
        for (int[] a:list) {
            int l=a[0];
            int r=a[1];
            if(r==n-1)break;
            pre += r-l+1;
            cnt += pre;
        }
        return cnt;
    }
}
