class Solution {
public:
    int maxOperations(string s) {
        if(is_sorted(s.begin(),s.end())){
            return 0;
        }

        int l = 0, c = 0, f = 0;
        for(int i = 0; i < s.size(); i++){
            if(s[i] == '1'){
                l++;
                f = 1;
            }
           if(s[i] == '0' && f){
               c += l;
               f = 0;
           }
        }
        return c;

    }
};
