class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int countZeros = 0, ans = 0;

        int j = n - 1;
        while (j >= 0) {
            if (s[j] == '0') {
                countZeros++;
                while (j >= 0 && s[j] == '0') {
                    j--;
                }
            }
            if (j >= 0 && s[j] == '1') {
                ans += countZeros;
                j--;
            }
        }

        return ans;
    }
};
