class Solution {
public:
    int maxOperations(string s) {
        int cur = 0, res = 0;
        bool flag = true;
        for (const char& ch : s) {
            if (ch == '1') {
                if (flag) {
                    res += cur;
                    flag = false;
                }
                cur++;
            } else {
                flag = true;
            }
            // cout << cur << ", " << res << endl;
        }
        if (flag) {
            res += cur;
        }
        return res;
    }
};
