class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, one = 0;
        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '0') {
                while (i < s.size() and s[i] == '0') {
                    i++;
                }
                i--;
                ans += one;
            } else {
                one++;
            }
        }

        return ans;
    }
};
