class Solution {
public:
    int maxOperations(string s) {
        int cnt=0,ans=0;
        bool ok=true;
        for(int i=0;i<s.size();i++){
            if(s[i]=='0'){
                if(ok) {
                    ans+=cnt;
                    ok=false;
                }
            }else{
                ok=true;
                cnt++;
            }
        }
        return ans;
    }
};
