class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        nz_runs = 0
        res = 0
        i = n - 1
        while True:
            if i < 0:
                break
            if s[i] == '0':
                nz_runs += 1
                while i >= 0 and s[i] == '0':
                    i -= 1
            else:
                res += nz_runs
                i -= 1
        return res
