class Solution {
    public int maxOperations(String s) {
        int res = 0;
        var ch = s.toCharArray();
        int count = 0;
        int i = 0;
        while (i < ch.length) {
            if (ch[i] == '1') {
                count++;
            } else {
                res += count;
                while (i + 1 < ch.length && ch[i + 1] == '0') {
                    i++;
                }
            }
            i++;
        }
        return res;
    }
}
