class Solution {
public:
    int maxOperations(string s) {
        int cnt = s[0]=='1', ans = 0;
        for( int i = 1 ; i < s.size() ; i++ ){
            if( s[i] == '0' && s[i-1] == '1' ){
                ans += cnt;
            }
            if( s[i] == '1' ) cnt++;
        }
        return ans;
    }
};
