public class Solution {
    public int MaxOperations(string s) {
        int result = 0;
        int onesCount = 0;
        int n = s.Length;
        for (int i = 0; i < n; i++)
        {
            if (i > 0 && s[i] == '0' && s[i-1] == '0')
            {
                continue;
            }
            if (s[i] == '1')
            {
                onesCount++;
            }
            else
            {
                result += onesCount;
            }
        }
        return result;
    }
}
