class Solution {
    public int maxOperations(String s) {
        int res = 0;
        int segment = 0;
        boolean contiguous = true;
        for (char chr : s.toCharArray()) {
            if (chr == '0') {
                if (contiguous) {
                    res += segment;
                    contiguous = false;
                }
            } else {
                contiguous = true;
                segment++;
            }
        }
        return res;
    }
}
