class Solution {
public:
    int maxOperations(string s) {
        int ans=0, ct=0;
        stack<char>stk;
        for(char c : s){
            if(stk.empty())stk.push(c);
            else{
                if(stk.top()=='0'){
                    if(c=='1')stk.push(c);
                }
                else stk.push(c);
            }
        }
        s="";
        while(!stk.empty()){
            s+=stk.top();
            stk.pop();
        }
        reverse(s.begin(),s.end());
        int n=s.size();
        for(int i=n-1;i>=0;i--){
            if(s[i]=='0')ct++;
            else ans+=ct;
        }
        return ans;
    }
};
