class Solution {
public:
    int maxOperations(string s) {
        int now=0;
        int ans=0;
        for(int i=0;i+1<s.size();i++){
            if(s[i]=='1')now++;
            else{
                if(s[i+1]=='1')ans+=now;
            }
        }
        if(s[s.size()-1]=='0')ans+=now;

        return ans;
    }
};
