class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int res = 0, last = 0;

        for (int i = n - 1; i >= 0; i--){
            if (s[i] == '1'){
                if (i == n - 1 || s[i + 1] == '1') res += last;
                else {
                    res += ++last;
                }
            }
        }
        return res;
    }
};
