class Solution {
public:
    int maxOperations(string s) {
        int res = 0, n = s.size(), i, ge = 0;
        reverse(s.begin(), s.end());
        for (i = 0; i < n; ++i)
            if (s[i] == '0'){
                if (i == 0 || s[i - 1] == '1') ++ge;
            }
            else res += ge;

        return res;
    }
};
