class Solution {
public:
  int maxOperations(string s) {
    string t;
    for (auto ch: s) {
      if (t.size() && t.back() == '0' && ch == '0') continue;
      t.push_back(ch);
    }
    int cur = 0;
    int ans = 0;
    while (t.size()) {
      auto ch = t.back(); t.pop_back();
      if (ch == '0') cur++;
      else ans += cur;
    }
    return ans;
  }
};
