class Solution {
public:
    int maxOperations(string s) {
        vector<int> num;
        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '1')
                if (i == 0 || s[i - 1] == '0')
                    num.push_back(1);
                else
                    num[num.size() - 1]++;
        }
        int ans = 0, x = 0;
        for (int i = 0; i < num.size(); i++) {
            if (i >= 1)
                ans += x;
            x += num[i];
        }
        if (s[s.size() - 1] != '1')
            ans += x;
        return ans;
    }
};
