const int N=1e5+10;
int f[N];
class Solution {
public:
    int maxOperations(string s) {
    memset(f,0,sizeof f);
    for(int i=s.size()-1;i>=0;i--)
    {
      f[i]=f[i+1];
      if(s[i]=='0'&&(s[i+1]=='1'||i+1==s.size()))
      f[i]++;
    }
    int res=0;
    for(int i=0;i<s.size();i++)
    {
    if(s[i]=='1')
    res+=f[i];
    }
    return res;
    }
};
