class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int n=s.length();
        int cnt=0;
        for(int i=0;i<n;i++) {
            cnt += s[i]=='1';
            if(i<n-1 and s[i]=='1' and s[i+1]=='0'){
                ans+=cnt;
                while(i<n and s[i]=='0'){
                    i++;
                }
            }
        }

        return ans;
    }
};
