class Solution {
    public int maxOperations(String s) {
        int count = 0;
        int n = s.length();
        int one = 0;
        for(int i=0;i<=n;i++){
            if(i==n || s.charAt(i)=='1'){
               if(i!=0 && s.charAt(i-1)=='0')  count+=one;
                one++;
            }            
        }
        return count;


    }
}
