class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        s = list(s)
        ans = 0
        c = sm = 0
        for i in range(n):
            if s[i] == '0':
                sm += c
                if c > 0:
                    ans += sm
                c = 0
            else:
                c += 1
            # print(i, c, sm)
        return ans
