#include <string>
using namespace std;

class Solution {
public:
    int maxOperations(string s) {
        int operations = 0;
        int n = s.size();
        int i = 0;

        int ones = 0;
        char prev = '=';
        for (int i = 0; i < n; ++i) {
            if (s[i] == '0' && prev == '1') {
                operations += ones;
            } else if (s[i] == '1') {
                ones ++;
            }
            // cout << prev << " " << ones << " " << operations << endl;
            prev = s[i];

        }

        return operations;
    }
};
