class Solution {
public:
    int maxOperations(string s) {
        vector<int> p;
        for(int i = 0; i<s.size(); i++) if(s[i] == '1') p.push_back(i);
        p.push_back(s.size());
        int ans = 0;
        for(int i = 1; i<p.size(); i++)
            if(p[i] != p[i-1]+1) ans+=i;
        return ans;
    }
};
