class Solution:
    def maxOperations(self, s: str) -> int:

        # 一次遍历得到答案

        number = 0
        res = 0
        index = 0
        n = len(s)

        while index < n:
            if s[index] == '1' or index == n - 1:
                res += number
                number += 1
                index += 1
                while index < n and s[index] == '1':
                    index += 1
                    number += 1
            else:
                index += 1

        return res
