function maxOperations(s: string): number {
  let r = 0;
  let k = 0; // 连续的 1
  let f = false; // 有 0 了

  for (let i = 0, l = s.length; i < l; ++i) {
    if (s[i] == "1") {
      if (k === 0) {
        k = 1;
        f = false;
      } else if (f) {
        r += k;
        k += 1;
        f = false;
      } else {
        k += 1;
      }
    } else {
      f = true;
    }

    // console.log(i, r);
  }

  if (f) {
    r += k;
  }

  return r;
}
