class Solution:
    def maxOperations(self, s: str) -> int:
        res = 0
        cnt = 0
        n = len(s)
        prev = ''
        for i,ch in enumerate(s):
            if ch == '1':
                cnt += 1
            else:
                if prev == '1':
                    res += cnt
            prev = ch
        return res
