class Solution:
    def maxOperations(self, s: str) -> int:
        arr = s.split('0')
        arr = [a for a in arr if a != '']
        if s[-1] == '0':
            arr.append('')
        res = 0
        cur = 0
        for i in range(len(arr)-1):
            cur += len(arr[i])
            res += cur
        return res
