class Solution {
public:
    int maxOperations(string s) {
        int ret = 0;
        int lst = 0;
        int n = (int)s.size();
        for(int i = n - 1; i >= 0; i--) {
            if(i < n - 1) {
                if(s[i] == '1' and s[i + 1] == '0') {
                    ret += (++lst);
                }
                else if(s[i] == '1') ret += lst;
            }
        }
        return ret;
    }
};
