class Solution {
public:
#define ll long long
    int maxOperations(string S) {
        ll ans = 0;
        int cnt = 0;
        for (int i = (int)S.size() - 2; i >= 0; i--){
            // block of 1s
            if (S[i] == '0' and S[i + 1] == '1'){
                cnt++;
            }

            if (S[i] == '1' and (!i or S[i - 1] == '0')){
                int x = i;
                while (x + 1 < S.size() and S[x + 1] == '1')
                    x++;

                ll rr = x - i + 1;
                ans += rr * (cnt + (S.back() == '0'));
            }
        }
        return ans;
    }
};
