class Solution {
public:
    int maxOperations(string s) {
        int cur = 0;
        int res = 0;
        for (int i = s.size() - 2; i >= 0; i--) {
            if (s[i] == '1') {
                cur += s[i + 1] == '0';
                res += cur;
            }
        }
        return res;
    }
};
