class Solution:
    def maxOperations(self, s: str) -> int:
        t = "*"
        for c in s:
            if c == '0' and c == t[-1]:
                continue
            t += c
        s = t[1:]
        ans = cur = 0
        for c in reversed(s):
            if c == '1':
                ans += cur
            else:
                cur += 1
        return ans
