class Solution {
public:

    int maxOperations(string s) {
        s += "1";
        long long ans = 0;
        int now = 0;
        int dis = 0;
        for(int i = 0;i < s.size();i ++ ) {
            if(s[i] == '1') {
                ans += now * dis;
                now ++;
                dis = 0;
            }else {
                dis = 1;
            }
        }
        return ans;
    }
};
