class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int n = s.length();
        int pre = 0;
        int tot = 0;
        for(int i = n - 1; i >= 0; i--) {
            int ch = s.charAt(i) - '0';
            if(ch == 0 && (i == n - 1 || s.charAt(i + 1) == '1')) {
                pre++;
            } 
            if(ch == 1) {
                tot += pre;
            }
        }
        return tot;
    }
}
