class Solution {
public:
    int maxOperations(string s) {
        long long ans = 0;
        int cnt = 0;
        for (int i = 0; i < s.size(); ++i){
            if (s[i] == '0' && (i + 1 == s.size() || s[i + 1] == '1')){
                ans += cnt;
            }
            cnt += s[i] == '1';
        }
        return ans;
    }
};
