class Solution {
public:
    int maxOperations(string s) {

        int res=0, fl=0;
        bool app=0;

        for(char &c:s){
            if(c=='1'){
                if(app){
                    res+=fl;
                }
                fl++;
                app=0;
            }
            else{
                app=1;
            }
        }
        if(app){
            res+=fl;
        }
        return res;
    }
};
