class Solution:
    def maxOperations(self, s: str) -> int:
        available = 0
        backup = 0
        operations = 0
        for i in range(len(s) - 1):
            if s[i] == '1' and s[i+1] == '0':
                available += 1
                available += backup
                operations += available
                backup = 0
            elif s[i] == '1':
                backup += 1
        return operations
